import React, { useState, useRef, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import "./Header.css";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navRef = useRef(null);
  const hamburgerRef = useRef(null);
  const { user, setUser } = useContext(UserContext);
  const navigate = useNavigate();

  // Zamknięcie menu po kliknięciu poza nim
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        isOpen &&
        navRef.current &&
        !navRef.current.contains(event.target) &&
        hamburgerRef.current &&
        !hamburgerRef.current.contains(event.target)
      ) {
        setIsOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [isOpen]);

  const handleAuthAction = () => {
    if (user) {
      // wylogowanie
      setUser(null);
      setIsOpen(false);
      navigate("/");
    } else {
      // logowanie
      setIsOpen(false);
      navigate("/volunteer-login");
    }
  };

  return (
    <header className="header">
      <div className="header-container">
        <Link to="/" className="logo">
          <img src="/img/logo.png" alt="Adoptuj Przyjaciela" />
        </Link>

        {/* Menu */}
        <nav ref={navRef} className={`nav ${isOpen ? "open" : ""}`}>
          <Link to="/zwierzaki-do-adopcji">Zwierzaki do adopcji</Link>
          <Link to="/czas-ze-zwierzakiem">Czas ze zwierzakiem</Link>
          <Link to="/help">Jak pomóc?</Link>
          <Link to="/wolontariat">Wolontariat</Link>
          <Link to="/blog">Blog</Link>
          <Link to="/o-nas">O nas</Link>
          <Link to="/kontakt">Kontakt</Link>

          {/* Przycisk w menu hamburgerowym */}
          <button className="login-btn auth-btn" onClick={handleAuthAction}>
            {user ? "Wyloguj" : "Zaloguj"}
          </button>
        </nav>

        {/* Hamburger */}
        <div
          ref={hamburgerRef}
          className={`hamburger ${isOpen ? "open" : ""}`}
          onClick={() => setIsOpen(!isOpen)}
        >
          <div></div>
          <div></div>
          <div></div>
        </div>

        {/* Desktop login */}
        <div className="desktop-login">
          <button className="login-btn auth-btn" onClick={handleAuthAction}>
            {user ? "Wyloguj" : "Zaloguj"}
          </button>
        </div>
      </div>

      {isOpen && <div className="nav-overlay" onClick={() => setIsOpen(false)} />}
    </header>
  );
};

export default Header;
