import React, { useState, useEffect } from "react";
import AnimalCard from "./AnimalCard";
import "./AnimalList.css";

const AnimalList = ({ animals }) => {
  const [visibleAnimals, setVisibleAnimals] = useState([]);
  const [showAll, setShowAll] = useState(false);

  // Funkcja ustawiająca ile kart ma być widocznych
  const updateVisible = () => {
    if (showAll) {
      setVisibleAnimals(animals);
    } else {
      const width = window.innerWidth;
      if (width < 570) {
        setVisibleAnimals(animals.slice(0, 4)); // mobilnie 4
      } else if (width < 992) {
        setVisibleAnimals(animals.slice(0, 6)); // tablet 6
      } else {
        setVisibleAnimals(animals); // desktop wszystkie
      }
    }
  };

  // Aktualizacja widocznych zwierzaków przy resize i mount
  useEffect(() => {
    updateVisible();
    window.addEventListener("resize", updateVisible);
    return () => window.removeEventListener("resize", updateVisible);
  }, [animals, showAll]);

  const handleShowMore = () => setShowAll(true);

  return (
    <div className="animal-list-wrapper">
      <div className="animal-list">
        {visibleAnimals.map((animal, i) => (
          <AnimalCard
            key={i}
            name={animal.name}
            gender={animal.gender}
            age={animal.age}
            size={animal.size}
            img={animal.img}
          />
        ))}
      </div>

      {!showAll && visibleAnimals.length < animals.length && (
        <button className="more-button" onClick={handleShowMore}>
          ZOBACZ WIĘCEJ ZWIERZAKÓW
        </button>
      )}
    </div>
  );
};

export default AnimalList;
