import React, { useState } from "react";
import animalsData from "../data/animalsData";
import postsData from "../data/postsData";
import "./AdminPanel.css";

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState("zwierzaki");
  const [animals, setAnimals] = useState(animalsData);
  const [posts, setPosts] = useState(postsData);

  const [newAnimal, setNewAnimal] = useState({
    name: "",
    type: "pies",
    description: "",
    photo: null
  });

  const [newPost, setNewPost] = useState({
    title: "",
    content: "",
    photo: null
  });

  // --- Zwierzaki ---
  const addAnimal = () => {
    if (!newAnimal.name || !newAnimal.photo) return;
    setAnimals([...animals, { ...newAnimal, id: Date.now() }]);
    setNewAnimal({ name: "", type: "pies", description: "", photo: null });
  };

  const deleteAnimal = (id) => {
    setAnimals(animals.filter(a => a.id !== id));
  };

  // --- Blog ---
  const addPost = () => {
    if (!newPost.title) return;
    setPosts([...posts, { ...newPost, id: Date.now() }]);
    setNewPost({ title: "", content: "", photo: null });
  };

  const deletePost = (id) => {
    setPosts(posts.filter(p => p.id !== id));
  };

  return (
    <div
      className="admin-page"
      style={{
        background: `linear-gradient(rgba(0,0,0,0.2), rgba(0,0,0,0.1)), url('/img/wolontariat2.jpg') center/cover no-repeat`
      }}
    >
      <div className="admin-panel-container">

        {/* Sidebar */}
        <div className="admin-sidebar">
          <h3 className="admin-panel-title">Panel Administratora</h3>
          <button className={activeTab==="zwierzaki"?"active":""} onClick={() => setActiveTab("zwierzaki")}>Zwierzaki</button>
          <button className={activeTab==="wiadomosci"?"active":""} onClick={() => setActiveTab("wiadomosci")}>Wiadomości</button>
          <button className={activeTab==="wolontariusze"?"active":""} onClick={() => setActiveTab("wolontariusze")}>Wolontariusze</button>
          <button className={activeTab==="blog"?"active":""} onClick={() => setActiveTab("blog")}>Blog</button>
        </div>

        {/* Panel główny */}
        <div className="admin-main">

          {/* Zwierzaki */}
          {activeTab === "zwierzaki" && (
            <div className="admin-section">
              <h2>Zwierzaki</h2>

              {/* Dodawanie nowego zwierzaka */}
              <div className="animal-form">
                <input
                  type="text"
                  placeholder="Imię zwierzaka"
                  value={newAnimal.name}
                  onChange={(e) => setNewAnimal({...newAnimal, name: e.target.value})}
                />
                <select
                  value={newAnimal.type}
                  onChange={(e) => setNewAnimal({...newAnimal, type: e.target.value})}
                >
                  <option value="pies">Pies</option>
                  <option value="kot">Kot</option>
                </select>
                <textarea
                  placeholder="Opis"
                  value={newAnimal.description}
                  onChange={(e) => setNewAnimal({...newAnimal, description: e.target.value})}
                />
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) =>
                    setNewAnimal({ ...newAnimal, photo: URL.createObjectURL(e.target.files[0]) })
                  }
                />
                <button onClick={addAnimal}>Dodaj zwierzaka</button>
              </div>

              {/* Lista zwierzaków */}
              <div className="animal-list">
                {animals.map((animal) => (
                  <div key={animal.id} className="animal-item">
                    {animal.photo && <img src={animal.photo} alt={animal.name} />}
                    <h4>{animal.name} ({animal.type})</h4>
                    <p>{animal.description}</p>
                    <div className="animal-buttons">
                      <button>Edytuj</button>
                      <button onClick={() => deleteAnimal(animal.id)}>Usuń</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Wiadomości */}
          {activeTab === "wiadomosci" && (
            <div className="admin-section">
              <h2>Wiadomości</h2>
              <p>Tu będą wyświetlane wiadomości od odwiedzających.</p>
            </div>
          )}

          {/* Wolontariusze */}
          {activeTab === "wolontariusze" && (
            <div className="admin-section">
              <h2>Wolontariusze</h2>
              <p>Lista wolontariuszy, akceptowanie aktywności i kontakt email.</p>
            </div>
          )}

          {/* Blog */}
          {activeTab === "blog" && (
            <div className="admin-section">
              <h2>Blog</h2>

              {/* Dodawanie nowego posta */}
              <div className="blog-form">
                <input
                  type="text"
                  placeholder="Tytuł posta"
                  value={newPost.title}
                  onChange={(e) => setNewPost({...newPost, title: e.target.value})}
                />
                <textarea
                  placeholder="Treść posta"
                  value={newPost.content}
                  onChange={(e) => setNewPost({...newPost, content: e.target.value})}
                />
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setNewPost({ ...newPost, photo: URL.createObjectURL(e.target.files[0]) })}
                />
                <button onClick={addPost}>Dodaj post</button>
              </div>

              {/* Lista postów */}
              <div className="blog-list">
                {posts.map((post) => (
                  <div key={post.id} className="blog-item">
                    {post.photo && <img src={post.photo} alt={post.title} />}
                    <h4>{post.title}</h4>
                    <p>{post.content.slice(0, 100)}...</p>
                    <div className="blog-buttons">
                      <button>Edytuj</button>
                      <button onClick={() => deletePost(post.id)}>Usuń</button>
                    </div>
                  </div>
                ))}
              </div>

            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
