import React, { useState } from "react";
import "./AdminPanel.css";

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState("dashboard");

  const renderContent = () => {
    switch (activeTab) {
      case "dashboard":
        return <DashboardView />;
      case "pets":
        return <PetsView />;
      case "volunteers":
        return <VolunteersView />;
      case "blog":
        return <BlogView />;
      case "messages":
        return <MessagesView />;
      default:
        return <DashboardView />;
    }
  };

  return (
    <div
      className="admin-page"
      style={{
        background: `linear-gradient(rgba(0,0,0,0.2), rgba(0,0,0,0.1)), url('/img/admintlo.jpg') center/cover no-repeat`,
      }}
    >
      <div className="admin-panel-container">
        <div className="admin-sidebar">
          <h2>Panel Admina</h2>
          <ul>
            <li onClick={() => setActiveTab("dashboard")}>Dashboard</li>
            <li onClick={() => setActiveTab("pets")}>Zwierzaki</li>
            <li onClick={() => setActiveTab("volunteers")}>Wolontariusze</li>
            <li onClick={() => setActiveTab("blog")}>Blog</li>
            <li onClick={() => setActiveTab("messages")}>Wiadomości</li>
          </ul>
        </div>
        <div className="admin-main">{renderContent()}</div>
      </div>
    </div>
  );
};

// --- Widoki ---
const DashboardView = () => <div><h3>Dashboard</h3><p>Statystyki, zadania wolontariuszy, wiadomości...</p></div>;
const PetsView = () => (
  <div>
    <h3>Zwierzaki</h3>
    <button>Dodaj nowego zwierzaka</button>
    <ul>
      <li>Pies – Azor</li>
      <li>Kot – Mruczek</li>
    </ul>
  </div>
);
const VolunteersView = () => <div><h3>Wolontariusze</h3></div>;
const BlogView = () => <div><h3>Blog</h3></div>;
const MessagesView = () => <div><h3>Wiadomości</h3></div>;

export default AdminPanel;
