// src/pages/BlogArticle.js
import React from "react"; import { useParams } from "react-router-dom"; import posts from "../data/postsData"; import "./BlogArticle.css"; // importujemy nowy plik CSS

const BlogArticle = () => { const { slug } = useParams(); const post = posts.find((p) => p.link.includes(slug));

 if (!post) return <p className="blog-article-not-found">Nie znaleziono artykułu</p>;
 
  return (
    <div style={{ padding: "2rem", maxWidth: "800px", margin: "auto" }}>
      <h1>{post.title}</h1>
      <img src={post.img} alt={post.title} style={{ width: "100%", margin: "1rem 0" }} />
      <p>{post.content}</p>
    </div>
  );
};

export default BlogArticle;
