import React, { useState } from "react";
import AnimalList from "../components/AnimalList";
import animalsData from "../data/animalsData";
import './AdoptionPage.css';


const AdoptionPage = () => {
  const [speciesFilter, setSpeciesFilter] = useState("Pies");
  const [sizeFilter, setSizeFilter] = useState("");
  const [ageFilter, setAgeFilter] = useState("");
  const [genderFilter, setGenderFilter] = useState("");

  const handleFilterChange = (filter, value) => {
    if (filter === "species") setSpeciesFilter(value);
    if (filter === "size") setSizeFilter(value);
    if (filter === "age") setAgeFilter(value);
    if (filter === "gender") setGenderFilter(value);
  };

  const filteredAnimals = animalsData.filter(animal => {
    return (
      (!speciesFilter || animal.species === speciesFilter) &&
      (!sizeFilter || animal.size === sizeFilter) &&
      (!ageFilter || animal.age === ageFilter) &&
      (!genderFilter || animal.gender === genderFilter)
    );
  });

  return (
    <div>
      <h2 style={{ textAlign: "center", color: "#ae5224" }}>Zwierzak do adopcji</h2>

      <div className="filters" style={{ textAlign: "center", marginBottom: "20px" }}>
        <div className="filters-row">
          <button onClick={() => handleFilterChange("species", "Pies")} className={speciesFilter === "Pies" ? "active" : ""}>Pies</button>
          <button onClick={() => handleFilterChange("species", "Kot")} className={speciesFilter === "Kot" ? "active" : ""}>Kot</button>
          <button onClick={() => handleFilterChange("species", "")} className={speciesFilter === "" ? "active" : ""}>Wszystkie</button>
        </div>

        <div className="filters-row">
          <select onChange={(e) => handleFilterChange("size", e.target.value)} value={sizeFilter}>
            <option value="">Wielkość</option>
            <option value="Mały">Mały</option>
            <option value="Średni">Średni</option>
            <option value="Duży">Duży</option>
          </select>

          <select onChange={(e) => handleFilterChange("age", e.target.value)} value={ageFilter}>
            <option value="">Wiek</option>
            <option value="Młody">Młody</option>
            <option value="Dorosły">Dorosły</option>
            <option value="Senior">Senior</option>
          </select>

          <select onChange={(e) => handleFilterChange("gender", e.target.value)} value={genderFilter}>
            <option value="">Płeć</option>
            <option value="On">On</option>
            <option value="Ona">Ona</option>
          </select>
        </div>
      </div>

      <AnimalList animals={filteredAnimals} />
    </div>
  );
};

export default AdoptionPage;
