import React from "react";
import "./AnimalCard.css";

const AnimalCard = ({ name, img, ageYears, ageMonths }) => {
  const formatAge = (years, months) => {
    let result = "";
    if (years > 0) result += `${years} ${years === 1 ? "rok" : "lata"} `;
    if (months > 0) result += `${months} ${months === 1 ? "miesiąc" : "miesięcy"}`;
    return result.trim();
  };

  return (
    <div className="animal-card">
      <img src={img} alt={name} />
      <h4>{name}</h4>
      {(ageYears !== undefined || ageMonths !== undefined) && (
        <p className="animal-age">{formatAge(ageYears || 0, ageMonths || 0)}</p>
      )}
    </div>
  );
};

export default AnimalCard;
