import React from "react";
import { useNavigate } from "react-router-dom";
import posts from "../data/postsData";
import "./BlogPage.css"; // opcjonalny plik CSS dla stylów

const BlogPage = () => {
  const navigate = useNavigate();

  // sortowanie od najnowszego do najstarszego (ostatni w tablicy jako najnowszy)
  const sortedPosts = [...posts].reverse();

  const handleReadMore = (link) => {
    navigate(link);
  };

  return (
    <div className="blog-page-wrapper">
      <h2 className="blog-page-title">Blog</h2>
      <div className="blog-list-page">
        {sortedPosts.map((post, i) => (
          <div key={i} className="blog-card-page">
            <img src={post.img} alt={post.title} className="blog-img-page" />
            <div className="blog-content-page">
              <h4>{post.title}</h4>
              <p>{post.excerpt}</p>
              <button className="adopt-button" onClick={() => handleReadMore(post.link)}>
                Czytaj więcej
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogPage;
