// Tablica wpisów na bloga
const posts = [
  { 
    title: "Jak przygotować się do adopcji psa?", 
    excerpt: "Adopcja psa to poważna decyzja, która wymaga odpowiedniego przygotowania. W artykule podpowiadamy, jakie rzeczy warto przygotować, jak zorganizować pierwsze dni w nowym domu i jak nawiązać silną więź z pupilem. Dowiesz się również, jakie pułapki mogą się pojawić i jak ich uniknąć, aby start był przyjemny zarówno dla Ciebie, jak i dla zwierzaka.", 
    img: "/img/dog-blog.jpg", 
    link: "/blog/jak-przygotowac-sie-do-adopcji-psa" 
  },
  { 
    title: "Dlaczego warto adoptować kota?", 
    excerpt: "Koty są wspaniałymi towarzyszami, ale decyzja o adopcji wymaga przemyślenia. Artykuł wyjaśnia korzyści emocjonalne i praktyczne płynące z adopcji kota, opisuje, jak przygotować dom i jak pomóc pupilowi zaaklimatyzować się w nowym otoczeniu. Poznasz też historie szczęśliwych adopcji, które mogą zainspirować do podjęcia tej pięknej decyzji.", 
    img: "/img/cat-blog.jpg", 
    link: "/blog/dlaczego-warto-adoptowac-kota" 
  },
  { 
    title: "Wskazówki dla nowych opiekunów", 
    excerpt: "Pierwsze dni z pupilem mogą być wyzwaniem – dlatego warto dobrze się przygotować. W artykule znajdziesz praktyczne wskazówki dotyczące organizacji domu, wyboru niezbędnych akcesoriów i budowania pozytywnych nawyków. Dowiesz się też, jak radzić sobie z lękiem separacyjnym, nauką czystości i integracją pupila z innymi członkami rodziny.", 
    img: "/img/opiekun.jpg", 
    link: "/blog/wskazowki-dla-nowych-opiekunow" 
  },
  { 
    title: "Historie adopcji", 
    excerpt: "Poznaj poruszające historie zwierząt, które znalazły nowy dom. Artykuł opisuje psy i koty, które przeszły trudne chwile w schronisku, a dzięki adopcji zyskały miłość i bezpieczeństwo. Dowiesz się, jak ich opiekunowie pokonali wyzwania pierwszych dni i jak wspólne życie zmieniło ich codzienność na lepsze.", 
    img: "/img/historie.jpg", 
    link: "/blog/historie-adopcji" 
  }
];

export default posts;
