import React, { useState } from "react";
import animalsData from "../data/animalsData";
import AdoptionAnimalCard from "../components/AdoptionAnimalCard";
import "./AdoptionPage.css";

const AdoptionPage = () => {
  // Stany tymczasowe do wyboru filtrów
  const [tempSpecies, setTempSpecies] = useState("");
  const [tempAge, setTempAge] = useState("");
  const [tempSize, setTempSize] = useState("");
  const [tempGender, setTempGender] = useState("");

  // Stany faktyczne filtrów
  const [speciesFilter, setSpeciesFilter] = useState("");
  const [ageFilter, setAgeFilter] = useState("");
  const [sizeFilter, setSizeFilter] = useState("");
  const [genderFilter, setGenderFilter] = useState("");

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 16;

  // Filtracja po kliknięciu "Filtruj"
  const filteredAnimals = animalsData.filter((animal) => {
    return (
      (speciesFilter === "" || animal.species === speciesFilter) &&
      (ageFilter === "" || animal.age === ageFilter) &&
      (sizeFilter === "" || animal.size === sizeFilter) &&
      (genderFilter === "" || animal.gender === genderFilter)
    );
  });

  // Pagina
  const totalPages = Math.ceil(filteredAnimals.length / itemsPerPage);
  const displayedAnimals = filteredAnimals.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const handleFilter = () => {
    setSpeciesFilter(tempSpecies);
    setAgeFilter(tempAge);
    setSizeFilter(tempSize);
    setGenderFilter(tempGender);
    setCurrentPage(1); // reset do pierwszej strony
  };

  const handleResetFilters = () => {
    setTempSpecies("");
    setTempAge("");
    setTempSize("");
    setTempGender("");
    setSpeciesFilter("");
    setAgeFilter("");
    setSizeFilter("");
    setGenderFilter("");
    setCurrentPage(1);
  };

  return (
    <div className="adoption-page">
      <h2 className="adoption-page__title">Zwierzęta do adopcji</h2>

      {/* Filtry */}
      <div className="adoption-page__filters">
        <div className="adoption-page__filter-group type-row">
          <button
            className={tempSpecies === "Pies" ? "active" : ""}
            onClick={() => setTempSpecies("Pies")}
          >
            Pies
          </button>
          <button
            className={tempSpecies === "Kot" ? "active" : ""}
            onClick={() => setTempSpecies("Kot")}
          >
            Kot
          </button>
          <button onClick={() => setTempSpecies("")}>Wszystkie</button>
        </div>

        <div className="adoption-page__filter-group traits-row">
          <select value={tempAge} onChange={(e) => setTempAge(e.target.value)}>
            <option value="">Wiek</option>
            <option value="Młody">Młody</option>
            <option value="Dorosły">Dorosły</option>
            <option value="Senior">Senior</option>
          </select>

          <select value={tempSize} onChange={(e) => setTempSize(e.target.value)}>
            <option value="">Rozmiar</option>
            <option value="Mały">Mały</option>
            <option value="Średni">Średni</option>
            <option value="Duży">Duży</option>
          </select>

          <select value={tempGender} onChange={(e) => setTempGender(e.target.value)}>
            <option value="">Płeć</option>
            <option value="On">On</option>
            <option value="Ona">Ona</option>
            <option value="One">One</option>
          </select>
        </div>

        <div className="adoption-page__filter-group search-row">
          <button className="search-button" onClick={handleFilter}>
            Filtruj
          </button>
          <button className="search-button reset-btn" onClick={handleResetFilters}>
            Reset
          </button>
        </div>
      </div>

      {/* Karty zwierzaków */}
      <div className="adoption-page__grid">
        {displayedAnimals.map((animal, index) => (
          <AdoptionAnimalCard key={index} {...animal} />
        ))}
      </div>

      {/* Pagina */}
      {totalPages > 1 && (
        <div className="adoption-page__pagination">
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              className={currentPage === i + 1 ? "active" : ""}
              onClick={() => setCurrentPage(i + 1)}
            >
              {i + 1}
            </button>
          ))}
          {currentPage < totalPages && (
            <button onClick={() => setCurrentPage(currentPage + 1)}>Następna</button>
          )}
        </div>
      )}
    </div>
  );
};

export default AdoptionPage;
