import React from "react";
import { useNavigate } from "react-router-dom";
import "./AnimalModal.css";

const AnimalModal = ({ animal, onClose, showAdoptionButton = false }) => {
  const navigate = useNavigate();

  if (!animal) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>
          ×
        </button>

        {/* Dwukolumnowy układ */}
        <img src={animal.img} alt={animal.name} />

        <div className="modal-info">
          <h3>{animal.name}</h3>
          <div className="animal-type">{animal.species}</div>
          <p><strong>Płeć:</strong> {animal.gender}</p>
          <p><strong>Wiek:</strong> {animal.age}</p>
          <p><strong>Wielkość:</strong> {animal.size}</p>
          {animal.shelterTime && (
            <p><strong>Czas w schronisku:</strong> {animal.shelterTime}</p>
          )}
          {animal.history && (
            <p><strong>Historia:</strong> {animal.history}</p>
          )}
          {animal.goodWithAnimals && (
            <p><strong>Może mieszkać z innymi zwierzętami:</strong> {animal.goodWithAnimals}</p>
          )}
          {animal.goodWithKids && (
            <p><strong>Może mieszkać z dziećmi:</strong> {animal.goodWithKids}</p>
          )}

          {showAdoptionButton && (
            <button
              className="modal-adopt-btn"
              onClick={() => navigate(`/adoption-contact/${animal.name}`)}
            >
              Skontaktuj się w sprawie adopcji
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default AnimalModal;
