import React, { useState } from "react";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import "./VolunteeringDashboard.css";

const dniTygodnia = ["Nd", "Pn", "Wt", "Śr", "Cz", "Pt", "Sb"];
const miesiace = [
  "Styczeń", "Luty", "Marzec", "Kwiecień", "Maj", "Czerwiec",
  "Lipiec", "Sierpień", "Wrzesień", "Październik", "Listopad", "Grudzień"
];

const VolunteeringDashboard = () => {
  // dzisiejsza data w formacie YYYY-MM-DD
  const today = new Date();
  const todayStr = `${today.getFullYear()}-${String(today.getMonth()+1).padStart(2,'0')}-${String(today.getDate()).padStart(2,'0')}`;

  const [selectedDate, setSelectedDate] = useState(todayStr);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    type: "",
    date: todayStr,
    time: "10:00"
  });

  const taskTypes = ["Spacer z psem", "Sprzątanie boksów", "Opieka nad zwierzętami"];
  const hours = Array.from({ length: 8 }, (_, i) => `${10 + i}:00`); // 10:00 - 17:00

  // funkcja do konwersji daty na lokalny format YYYY-MM-DD
  const formatLocalDate = (date) => {
    const d = new Date(date);
    return `${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,'0')}-${String(d.getDate()).padStart(2,'0')}`;
  };

  const addTask = () => {
    if (!newTask.type || !newTask.date || !newTask.time) return;

    setTasks([...tasks, { ...newTask, status: "pending" }]);
    setNewTask({ type: "", date: selectedDate, time: "10:00" });
  };

  const cancelTask = (index) => {
    const reason = prompt("Podaj powód rezygnacji:");
    if (!reason) return;

    const updated = [...tasks];
    updated[index].status = "cancelled";
    updated[index].reason = reason;
    setTasks(updated);
  };

  const tasksForDay = tasks.filter((t) => t.date === selectedDate);

  const formatDate = (dateStr) => {
    const [year, month, day] = dateStr.split("-").map(Number);
    const dateObj = new Date(year, month - 1, day);
    return `${dniTygodnia[dateObj.getDay()]}, ${day} ${miesiace[month - 1]} ${year}`;
  };

  return (
    <div className="dashboard-container">
      <h2>Panel wolontariusza</h2>

      {/* Kalendarz */}
      <div className="calendar-box">
        <Calendar
          onChange={(date) => {
            const d = formatLocalDate(date);
            setSelectedDate(d);
            setNewTask({ ...newTask, date: d });
          }}
          value={new Date(selectedDate)}
          locale="pl-PL"
        />
      </div>

      {/* Lista zadań */}
      <div className="task-box">
        <h3>Zadania na {formatDate(selectedDate)}</h3>
        <ul>
          {tasksForDay.length > 0 ? (
            tasksForDay.map((task, index) => (
              <li key={index} className={`task-item ${task.status}`}>
                {task.type} — {task.time}
                {task.status === "pending" && <span> Oczekuje na zatwierdzenie</span>}
                {task.status === "approved" && <span> Zatwierdzone ✔</span>}
                {task.status === "cancelled" && <span> Anulowane — {task.reason}</span>}
                {task.status !== "cancelled" && (
                  <button
                    onClick={() => cancelTask(tasks.indexOf(task))}
                    className="cancel-btn"
                  >
                    Usuń
                  </button>
                )}
              </li>
            ))
          ) : (
            <li>Brak zadań na ten dzień</li>
          )}
        </ul>
      </div>

      {/* Dodawanie zadania */}
      <div className="add-task-box">
        <h3>Dodaj aktywność</h3>
        <select
          value={newTask.type}
          onChange={(e) => setNewTask({ ...newTask, type: e.target.value })}
        >
          <option value="">-- wybierz aktywność --</option>
          {taskTypes.map((t) => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>

        <input
          type="date"
          value={newTask.date}
          onChange={(e) => setNewTask({ ...newTask, date: e.target.value })}
        />

        <select
          value={newTask.time}
          onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
        >
          {hours.map(h => (
            <option key={h} value={h}>{h}</option>
          ))}
        </select>

        <button className="add-btn" onClick={addTask}>Dodaj</button>
      </div>
    </div>
  );
};

export default VolunteeringDashboard;
