import React, { useState } from "react";
import animalsData from "../data/animalsData";
import "./AdoptionPage.css";

const AdoptionPage = () => {
  const [speciesFilter, setSpeciesFilter] = useState("");
  const [ageFilter, setAgeFilter] = useState("");
  const [sizeFilter, setSizeFilter] = useState("");
  const [genderFilter, setGenderFilter] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 16;

  // Filtracja zwierzaków
  const filteredAnimals = animalsData.filter((animal) => {
    return (
      (speciesFilter === "" || animal.species === speciesFilter) &&
      (ageFilter === "" || animal.age === ageFilter) &&
      (sizeFilter === "" || animal.size === sizeFilter) &&
      (genderFilter === "" || animal.gender === genderFilter)
    );
  });

  // Pagina
  const totalPages = Math.ceil(filteredAnimals.length / itemsPerPage);
  const displayedAnimals = filteredAnimals.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const handleFilter = () => {
    setCurrentPage(1); // reset do pierwszej strony po filtrowaniu
  };

  return (
    <div className="adoption-page">
      <h2 className="page-title">Zwierzęta do adopcji</h2>

      <div className="filters">
        {/* Przyciski gatunków */}
        <div className="filter-group">
          <button
            className={speciesFilter === "Pies" ? "active" : ""}
            onClick={() => setSpeciesFilter("Pies")}
          >
            Pies
          </button>
          <button
            className={speciesFilter === "Kot" ? "active" : ""}
            onClick={() => setSpeciesFilter("Kot")}
          >
            Kot
          </button>
          <button onClick={() => setSpeciesFilter("")}>Wszystkie</button>
        </div>

        {/* Selecty */}
        <div className="filter-group">
          <select value={ageFilter} onChange={(e) => setAgeFilter(e.target.value)}>
            <option value="">Wiek</option>
            <option value="Młody">Młody</option>
            <option value="Dorosły">Dorosły</option>
            <option value="Senior">Senior</option>
          </select>

          <select value={sizeFilter} onChange={(e) => setSizeFilter(e.target.value)}>
            <option value="">Rozmiar</option>
            <option value="Mały">Mały</option>
            <option value="Średni">Średni</option>
            <option value="Duży">Duży</option>
          </select>

          <select value={genderFilter} onChange={(e) => setGenderFilter(e.target.value)}>
            <option value="">Płeć</option>
            <option value="On">On</option>
            <option value="Ona">Ona</option>
            <option value="One">One</option>
          </select>
        </div>

        {/* Przycisk filtruj */}
        <div className="filter-group">
          <button className="filter-btn" onClick={handleFilter}>
            Filtruj
          </button>
        </div>
      </div>

      {/* Karty zwierzaków */}
      <div className="animal-grid">
        {displayedAnimals.map((animal, index) => (
          <div key={index} className="animal-card">
            <img src={animal.img} alt={animal.name} />
            <h3>{animal.name}</h3>
            <div className="animal-info">
              <span>Wiek: {animal.age}</span>
              <span>Rozmiar: {animal.size}</span>
              <span>Płeć: {animal.gender}</span>
            </div>
          </div>
        ))}
      </div>

      {/* Pagina */}
      {totalPages > 1 && (
        <div className="pagination">
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              className={currentPage === i + 1 ? "active" : ""}
              onClick={() => setCurrentPage(i + 1)}
            >
              {i + 1}
            </button>
          ))}
          {currentPage < totalPages && (
            <button onClick={() => setCurrentPage(currentPage + 1)}>Następna</button>
          )}
        </div>
      )}
    </div>
  );
};

export default AdoptionPage;
