import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./AdoptionContact.css";

const AdoptionContact = () => {
  const { animalName } = useParams();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    date: "",
    time: "",
    message: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Formularz wys≈Çany:", formData);
    setSubmitted(true);
    setFormData({
      name: "",
      email: "",
      phone: "",
      date: "",
      time: "",
      message: "",
    });
  };

  return (
    <div className="adoption-contact">
      <h2>Formularz kontaktowy w sprawie {animalName}</h2>

      {submitted ? (
        <div className="form-success">
          <p>Dziƒôkujemy! Twoja wiadomo≈õƒá zosta≈Ça wys≈Çana üêæ</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Twoje imiƒô i nazwisko"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Tw√≥j email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="tel"
            name="phone"
            placeholder="Telefon (opcjonalnie)"
            value={formData.phone}
            onChange={handleChange}
          />

          <label>Wybierz preferowany dzie≈Ñ wizyty:</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
            min={new Date().toISOString().split("T")[0]}
          />

          <label>Wybierz godzinƒô (10:00 - 18:00):</label>
          <input
            type="time"
            name="time"
            value={formData.time}
            onChange={handleChange}
            required
            min="10:00"
            max="18:00"
          />

          <textarea
            name="message"
            placeholder="Twoja wiadomo≈õƒá"
            value={formData.message}
            onChange={handleChange}
            rows="5"
          ></textarea>

          <button type="submit">Wy≈õlij wiadomo≈õƒá</button>
        </form>
      )}
    </div>
  );
};

export default AdoptionContact;
