import React from "react";
import "./BlogSection.css";

const BlogSection = ({ posts }) => {
  const handleSeeMore = () => {
    // przekierowanie do pełnej strony bloga
    window.location.href = "/blog"; 
    // lub jeśli używasz react-router-dom: useNavigate
    // navigate("/blog");
  };

  return (
    <section className="blog-section">
      <h3>Ostatnie wpisy na blogu</h3>
      <div className="blog-list">
        {posts.slice(0, 4).map((post, i) => (
          <div key={i} className="blog-card">
            <img src={post.img} alt={post.title} className="blog-img" />
            <div className="blog-content">
              <h4>{post.title}</h4>
              <p>{post.excerpt}</p>
              <a href={post.link} className="blog-link">Czytaj więcej</a>
            </div>
          </div>
        ))}
      </div>

      {/* Przycisk Zobacz więcej wpisów */}
      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <button className="adopt-button" onClick={handleSeeMore}>
          Zobacz więcej wpisów
        </button>
      </div>
    </section>
  );
};

export default BlogSection;
