import React, { useState } from "react";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import "./VolunteeringDashboard.css";

// pomocnicze do polskich nazw dni i miesięcy
const dniTygodnia = ["Nd", "Pn", "Wt", "Śr", "Cz", "Pt", "Sb"];
const miesiace = [
  "Styczeń", "Luty", "Marzec", "Kwiecień", "Maj", "Czerwiec",
  "Lipiec", "Sierpień", "Wrzesień", "Październik", "Listopad", "Grudzień"
];

const VolunteeringDashboard = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    type: "",
    date: "",
    time: "10:00",
    duration: "0.5h"
  });

  const taskTypes = [
    "Spacer z psem",
    "Sprzątanie boksów",
    "Opieka nad zwierzętami",
  ];

  const durations = ["0.5h", "1h", "1.5h", "2h", ">2h"];
  const hours = Array.from({ length: 8 }, (_, i) => `${10 + i}:00`);

  const addTask = () => {
    if (!newTask.type || !newTask.date || !newTask.time || !newTask.duration) return;

    setTasks([...tasks, { ...newTask, status: "pending" }]);
    setNewTask({ type: "", date: selectedDate.toISOString().split("T")[0], time: "10:00", duration: "0.5h" });
  };

  const cancelTask = (index) => {
    const reason = prompt("Podaj powód rezygnacji:");
    if (!reason) return;

    const updated = [...tasks];
    updated[index].status = "cancelled";
    updated[index].reason = reason;

    setTasks(updated);
  };

  // filtrujemy zadania dla wybranego dnia
  const tasksForDay = tasks.filter(
    (t) => t.date === selectedDate.toISOString().split("T")[0]
  );

  // format daty po polsku
  const formatDate = (date) => {
    const day = date.getDate();
    const month = miesiace[date.getMonth()];
    const dayName = dniTygodnia[date.getDay()];
    return `${dayName}, ${day} ${month} ${date.getFullYear()}`;
  };

  return (
    <div className="dashboard-container">
      <h2>Panel wolontariusza</h2>

      <div className="calendar-box">
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
          locale="pl-PL"
        />
      </div>

      <div className="task-box">
        <h3>Zadania na {formatDate(selectedDate)}</h3>
        <ul>
          {tasksForDay.length > 0 ? (
            tasksForDay.map((task, index) => (
              <li key={index} className={`task-item ${task.status}`}>
                {task.type} — {task.time} ({task.duration})
                {task.status === "pending" && <span> Oczekuje na zatwierdzenie</span>}
                {task.status === "approved" && <span> Zatwierdzone ✔</span>}
                {task.status === "cancelled" && <span> Anulowane — {task.reason}</span>}
                {task.status !== "cancelled" && (
                  <button
                    onClick={() => cancelTask(tasks.indexOf(task))}
                    className="cancel-btn"
                  >
                    Usuń
                  </button>
                )}
              </li>
            ))
          ) : (
            <li>Brak zadań na ten dzień</li>
          )}
        </ul>
      </div>

      <div className="add-task-box">
        <h3>Dodaj aktywność</h3>
        <select
          value={newTask.type}
          onChange={(e) => setNewTask({ ...newTask, type: e.target.value })}
        >
          <option value="">-- wybierz aktywność --</option>
          {taskTypes.map((t) => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>

        <input
          type="date"
          value={newTask.date || selectedDate.toISOString().split("T")[0]}
          onChange={(e) => setNewTask({ ...newTask, date: e.target.value })}
        />

        <select
          value={newTask.time}
          onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
        >
          {hours.map(h => (
            <option key={h} value={h}>{h}</option>
          ))}
        </select>

        <select
          value={newTask.duration}
          onChange={(e) => setNewTask({ ...newTask, duration: e.target.value })}
        >
          {durations.map(d => (
            <option key={d} value={d}>{d}</option>
          ))}
        </select>

        <button className="add-btn" onClick={addTask}>
          Dodaj
        </button>
      </div>
    </div>
  );
};

export default VolunteeringDashboard;
