import React, { useState } from "react";
import "./VolunteeringDashboard.css";

const VolunteeringDashboard = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ type: "", date: "" });

  const taskTypes = [
    "Spacer z psem",
    "Sprzątanie boksów",
    "Opieka nad zwierzętami",
  ];

  const addTask = () => {
    if (!newTask.type || !newTask.date) return;

    setTasks([
      ...tasks,
      { ...newTask, status: "pending" } 
    ]);

    setNewTask({ type: "", date: "" });
  };

  const cancelTask = (index) => {
    const reason = prompt("Podaj powód rezygnacji:");
    if (!reason) return;

    const updated = [...tasks];
    updated[index].status = "cancelled";
    updated[index].reason = reason;

    setTasks(updated);
  };

  return (
    <div className="dashboard-container">
      <h2>Panel wolontariusza</h2>

      <div className="calendar-box">
        <h3>Wybierz dzień:</h3>
        <input type="date" onChange={(e) => setSelectedDate(e.target.value)} />
      </div>

      {selectedDate && (
        <div className="task-box">
          <h3>Zadania na {selectedDate}</h3>
          <ul>
            {tasks
              .filter((t) => t.date === selectedDate)
              .map((task, index) => (
                <li key={index} className={`task-item ${task.status}`}>
                  {task.type} —
                  {task.status === "pending" && (
                    <span> Oczekuje na zatwierdzenie</span>
                  )}
                  {task.status === "approved" && (
                    <span> Zatwierdzone ✔</span>
                  )}
                  {task.status === "cancelled" && (
                    <span> Anulowane — {task.reason}</span>
                  )}

                  {task.status !== "cancelled" && (
                    <button
                      onClick={() => cancelTask(index)}
                      className="cancel-btn"
                    >
                      Usuń
                    </button>
                  )}
                </li>
              ))}
          </ul>
        </div>
      )}

      <div className="add-task-box">
        <h3>Dodaj aktywność</h3>

        <select
          value={newTask.type}
          onChange={(e) =>
            setNewTask({ ...newTask, type: e.target.value })
          }
        >
          <option value="">-- wybierz --</option>
          {taskTypes.map((t) => (
            <option key={t} value={t}>
              {t}
            </option>
          ))}
        </select>

        <input
          type="date"
          value={newTask.date}
          onChange={(e) =>
            setNewTask({ ...newTask, date: e.target.value })
          }
        />

        <button className="add-btn" onClick={addTask}>
          Dodaj
        </button>
      </div>
    </div>
  );
};

export default VolunteeringDashboard;
