import React, { useState } from "react";

const VolunteeringRegister = () => {
  const [formData, setFormData] = useState({
    name: "",
    birthDate: "",
    phone: "",
    email: "",
    password: "",
    guardianName: "",
    guardianEmail: "",
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const today = new Date();
    const birthDate = new Date(formData.birthDate);
    const age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }

    if (age < 18 && age >= 16 && (!formData.guardianName || !formData.guardianEmail)) {
      setError("Jeśli masz mniej niż 18 lat, musisz podać dane opiekuna.");
      return;
    }

    if (age < 16) {
      setError("Użytkownik poniżej 16 lat może uczestniczyć tylko z opiekunem.");
      return;
    }

    setError("");
    console.log("Rejestracja:", formData);
    alert("Konto zostało utworzone!");
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <h2>Rejestracja wolontariusza</h2>

        <form onSubmit={handleSubmit} className="auth-form">
          <label>Imię:</label>
          <input type="text" name="name" required onChange={handleChange} />

          <label>Data urodzenia:</label>
          <input type="date" name="birthDate" required onChange={handleChange} />

          <label>Numer telefonu:</label>
          <input type="tel" name="phone" required onChange={handleChange} />

          <label>Email:</label>
          <input type="email" name="email" required onChange={handleChange} />

          <label>Hasło:</label>
          <input type="password" name="password" required onChange={handleChange} />

          {formData.birthDate && new Date().getFullYear() - new Date(formData.birthDate).getFullYear() < 18 && (
            <>
              <label>Imię opiekuna:</label>
              <input type="text" name="guardianName" onChange={handleChange} />
              
              <label>Email opiekuna:</label>
              <input type="email" name="guardianEmail" onChange={handleChange} />
            </>
          )}

          {error && <p style={{ color: "red", fontSize: "0.9rem" }}>{error}</p>}

          <button type="submit" className="auth-btn">Utwórz konto</button>
        </form>

        <p className="auth-switch">
          Masz już konto? <a href="/volunteering-login">Zaloguj się</a>
        </p>
      </div>
    </div>
  );
};

export default VolunteeringRegister;
