// src/pages/BlogArticle.js
import React from "react";
import { useParams } from "react-router-dom";
import posts from "../data/postsData";
import "./BlogArticle.css"; // importujemy nowy plik CSS

const BlogArticle = () => {
  const { slug } = useParams();
  const post = posts.find((p) => p.link.includes(slug));

  if (!post) return <p className="blog-article-not-found">Nie znaleziono artykułu</p>;

  return (
    <div className="blog-article-wrapper">
  <h1 className="blog-article-title">{post.title}</h1>
  {post.contentBlocks?.map((block, i) => (
    <div key={i} className="blog-article-block">
      <img src={block.img} alt={`${post.title} ${i+1}`} className="blog-article-img" />
      <p className="blog-article-content">{block.text}</p>
    </div>
  ))}
</div>

  );
};

export default BlogArticle;
