const handleFilter = () => {
  const parseAge = (ageStr) => {
    if (!ageStr) return 0;
    if (ageStr.includes("miesięcy") || ageStr.includes("miesiąc")) {
      return parseInt(ageStr); // w miesiącach
    }
    if (ageStr.includes("lat") || ageStr.includes("rok")) {
      return parseInt(ageStr) * 12; // konwersja lat na miesiące
    }
    return 0;
  };

  const result = animalsData.filter((animal) => {
    // filtr gatunku
    const speciesMatch =
      speciesFilter === "" || speciesFilter === "Wszystkie" || animal.species === speciesFilter;

    // filtr wieku
    let ageMatch = true;
    const ageInMonths = parseAge(animal.age);
    if (ageFilter === "Młody") {
      ageMatch = ageInMonths <= 12;
    } else if (ageFilter === "Dorosły") {
      ageMatch = ageInMonths > 12 && ageInMonths <= 84; // 1-7 lat
    } else if (ageFilter === "Senior") {
      ageMatch = ageInMonths > 84; // powyżej 7 lat
    }

    // filtr wielkości
    const sizeMatch = sizeFilter === "" || animal.size === sizeFilter;

    // filtr płci
    const genderMatch = genderFilter === "" || animal.gender === genderFilter;

    return speciesMatch && ageMatch && sizeMatch && genderMatch;
  });

  setFilteredAnimals(result);
  setCurrentPage(1);
  setAgeFilter("");
  setSizeFilter("");
  setGenderFilter("");
};
