import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import "./VolunteeringDashboard.css";

const dniTygodnia = ["Nd", "Pn", "Wt", "Åšr", "Cz", "Pt", "Sb"];
const miesiace = [
  "StyczeÅ„", "Luty", "Marzec", "KwiecieÅ„", "Maj", "Czerwiec",
  "Lipiec", "SierpieÅ„", "WrzesieÅ„", "PaÅºdziernik", "Listopad", "GrudzieÅ„"
];

const VolunteeringDashboard = () => {
  const navigate = useNavigate();

  // ðŸ”¹ Sprawdzenie zalogowanego uÅ¼ytkownika
  const [user, setUser] = useState(null);
  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (!savedUser) {
      navigate("/volunteering-login"); // przekierowanie jeÅ›li niezalogowany
    } else {
      setUser(JSON.parse(savedUser));
    }
  }, [navigate]);

  const today = new Date();
  const todayStr = today.toISOString().split("T")[0];

  const [selectedDate, setSelectedDate] = useState(todayStr);
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    type: "",
    date: todayStr,
    time: "10:00",
    note: ""
  });

  const taskTypes = ["Spacer z psem", "SprzÄ…tanie boksÃ³w", "Opieka nad zwierzÄ™tami"];
  const hours = Array.from({ length: 8 }, (_, i) => `${10 + i}:00`);

  const addTask = () => {
    if (!newTask.type || !newTask.date || !newTask.time) return;

    setTasks([...tasks, { ...newTask, status: "pending" }]);
    setNewTask({ type: "", date: selectedDate, time: "10:00", note: "" });
  };

  const cancelTask = (index) => {
    const reason = prompt("Podaj powÃ³d rezygnacji:");
    if (!reason) return;

    const updated = [...tasks];
    updated[index].status = "cancelled";
    updated[index].reason = reason;
    setTasks(updated);
  };

  const tasksForDay = tasks.filter((t) => t.date === selectedDate);

  const formatDate = (dateStr) => {
    const [year, month, day] = dateStr.split("-").map(Number);
    const dateObj = new Date(year, month - 1, day);
    return `${dniTygodnia[dateObj.getDay()]}, ${day} ${miesiace[month - 1]} ${year}`;
  };

  const tileContent = ({ date, view }) => {
    if (view === "month") {
      const dStr = `${date.getFullYear()}-${(date.getMonth()+1).toString().padStart(2,'0')}-${date.getDate().toString().padStart(2,'0')}`;
      const hasTask = tasks.some(t => t.date === dStr);
      return hasTask ? <div className="dot"></div> : null;
    }
  };

  return (
    <div className="dashboard-container">
      {user && <h2>Panel wolontariusza â€” Witaj, {user.email}!</h2>}

      {/* Kalendarz */}
      <div className="calendar-box">
        <Calendar
          onChange={(date) => {
            const year = date.getFullYear();
            const month = date.getMonth() + 1;
            const day = date.getDate();
            const d = `${year}-${month.toString().padStart(2,'0')}-${day.toString().padStart(2,'0')}`;
            setSelectedDate(d);
            setNewTask({ ...newTask, date: d });
          }}
          value={new Date(selectedDate)}
          locale="pl-PL"
          tileContent={tileContent}
        />
      </div>

      {/* Lista zadaÅ„ */}
      <div className="task-box">
        <div className="task-header">Lista zadaÅ„</div>
        <div className="task-date">{formatDate(selectedDate)}</div>
        {tasksForDay.length > 0 ? (
          tasksForDay.map((task, index) => (
            <div key={index} className={`task-item ${task.status}`}>
              <div className="task-main-row">
                <div className="task-info">{task.type} â€” {task.time}</div>
                <div className="task-status">
                  {task.status === "pending" && "Oczekuje na zatwierdzenie"}
                  {task.status === "approved" && "Zatwierdzone âœ”"}
                  {task.status === "cancelled" && `Anulowane â€” ${task.reason}`}
                </div>
                {task.status !== "cancelled" && (
                  <button
                    onClick={() => cancelTask(tasks.indexOf(task))}
                    className="cancel-btn"
                  >
                    UsuÅ„
                  </button>
                )}
              </div>
              {task.note && <div className="task-note">{task.note}</div>}
            </div>
          ))
        ) : (
          <div>Brak zadaÅ„ na ten dzieÅ„</div>
        )}
      </div>

      {/* Dodawanie zadania */}
      <div className="add-task-box">
        <div className="add-task-header">Dodaj aktywnoÅ›Ä‡</div>
        <div className="add-task-row">
          <select
            value={newTask.type}
            onChange={(e) => setNewTask({ ...newTask, type: e.target.value })}
          >
            <option value="">-- wybierz aktywnoÅ›Ä‡ --</option>
            {taskTypes.map((t) => (
              <option key={t} value={t}>{t}</option>
            ))}
          </select>

          <input
            type="date"
            value={newTask.date}
            onChange={(e) => setNewTask({ ...newTask, date: e.target.value })}
          />

          <select
            value={newTask.time}
            onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
          >
            {hours.map(h => (
              <option key={h} value={h}>{h}</option>
            ))}
          </select>

          <button className="add-btn" onClick={addTask}>Dodaj</button>
        </div>

        <textarea
          className="add-task-note"
          placeholder="Dodaj notatkÄ™..."
          value={newTask.note}
          onChange={(e) => setNewTask({ ...newTask, note: e.target.value })}
        />
      </div>
    </div>
  );
};

export default VolunteeringDashboard;
