import React from "react";
import { useParams } from "react-router-dom";
import posts from "../data/postsData";
import "./BlogArticle.css"; // importujemy nowy plik CSS const BlogArticle = () => { const { slug } = useParams(); const post = posts.find((p) => p.link.includes(slug)); if (!post) return <p className="blog-article-not-found">Nie znaleziono artykułu</p>; return ( <div className="blog-article-wrapper"> <h1 className="blog-article-title">{post.title}</h1> <div className="blog-article-images"> {post.images?.map((src, i) => ( <img key={i} src={src} alt={${post.title} ${i+1}} className="blog-article-img" /> ))} </div> <p className="blog-article-content">{post.content}</p> </div> ); }; export default BlogArticle;
